<script>
    document.addEventListener('DOMContentLoaded', function() {
        const tabLinks = document.querySelectorAll('.tab-link');
        const contentDiv = document.getElementById('con1');




        tabLinks.forEach(function(link) {
            link.addEventListener('click', function(event) {
                event.preventDefault();
                const targetTab = this.getAttribute('data-target');
                contentDiv.textContent = 'Content for Tab 1';
                // Handle tab-specific content changes
                if (targetTab === 'tab1') {
                    contentDiv.textContent = 'Content for Tab 1';
                } else if (targetTab === 'tab2') {
                    contentDiv.textContent = 'Content for Tab 2';
                }

                // Optional: Remove active class from all tabs and add it to the clicked tab
                tabLinks.forEach(function(tab) {
                    tab.classList.remove('active');
                });
                this.classList.add('active');
            });
        });
    });
</script>
